new Vue({el:"#app",data:{playerHealth:100,monsterHealth:100,gameIsRunning:!1,turns:[]},methods:{startGame:function(){this.gameIsRunning=!0,this.playerHealth=100,this.monsterHealth=100,this.turns=[]},attack:function(){var t=this.calculateDamage(3,10);this.monsterHealth-=t,this.turns.unshift({isPlayer:!0,text:"Player hits Monster for"+t}),this.checkWin()||this.monsterAttacks()},specialAttack:function(){var t=this.calculateDamage(10,20);this.monsterHealth-=t,this.turns.unshift({isPlayer:!0,text:"Player hits Monster hard for"+t}),this.checkWin()||this.monsterAttacks()},heal:function(){this.playerHealth<=90?this.playerHealth+=10:this.playerHealth=100,this.turns.unshift({isPlayer:!0,text:"Player heals for 10"}),this.monsterAttacks()},giveUp:function(){this.gameIsRunning=!1},monsterAttacks:function(){var t=this.calculateDamage(5,12);this.playerHealth-=t,this.checkWin(),this.turns.unshift({isPlayer:!1,text:"Monster hits Player for"+t})},calculateDamage:function(t,a){return Math.max(Math.floor(Math.random()*a)+1,t)},checkWin:function(){return this.monsterHealth<=0?(confirm("You win! New game?")?this.startGame():this.gameIsRunning=!1,!0):this.playerHealth<=0&&(confirm("You lost! New game?")?this.startGame():this.gameIsRunning=!1,!0)}}});